const BOARD_WIDTH = 10;
const BOARD_HEIGHT = 20;
const BLOCK_SIZE = 30;

class TetrisGame {
    constructor() {
        this.canvas = document.getElementById('game-canvas');
        this.ctx = this.canvas.getContext('2d');
        this.nextCanvas = document.getElementById('next-canvas');
        this.nextCtx = this.nextCanvas.getContext('2d');

        this.board = Array(BOARD_HEIGHT).fill().map(() => Array(BOARD_WIDTH).fill(0));
        this.currentPiece = null;
        this.nextPiece = null;
        this.score = 0;
        this.level = 1;
        this.lines = 0;
        this.dropTime = 0;
        this.dropInterval = 1000;
        this.gameRunning = false;
        this.paused = false;

        this.init();
    }

    init() {
        this.currentPiece = new TetrisPiece();
        this.nextPiece = new TetrisPiece();
        this.gameRunning = true;
        this.updateDisplay();
        this.drawNextPiece();
    }

    update(deltaTime) {
        if (!this.gameRunning || this.paused) return;

        this.dropTime += deltaTime;
        if (this.dropTime > this.dropInterval) {
            this.moveDown();
            this.dropTime = 0;
        }
    }

    moveDown() {
        if (this.currentPiece.canMoveTo(
            this.currentPiece.x, 
            this.currentPiece.y + 1, 
            this.currentPiece.shape, 
            this.board
        )) {
            this.currentPiece.y++;
        } else {
            this.placePiece();
            this.clearLines();
            this.spawnNewPiece();
        }
        this.draw();
    }

    moveLeft() {
        if (this.currentPiece.canMoveTo(
            this.currentPiece.x - 1, 
            this.currentPiece.y, 
            this.currentPiece.shape, 
            this.board
        )) {
            this.currentPiece.x--;
            this.draw();
        }
    }

    moveRight() {
        if (this.currentPiece.canMoveTo(
            this.currentPiece.x + 1, 
            this.currentPiece.y, 
            this.currentPiece.shape, 
            this.board
        )) {
            this.currentPiece.x++;
            this.draw();
        }
    }

    rotate() {
        const rotatedShape = this.currentPiece.rotate();
        if (this.currentPiece.canMoveTo(
            this.currentPiece.x, 
            this.currentPiece.y, 
            rotatedShape, 
            this.board
        )) {
            this.currentPiece.shape = rotatedShape;
            this.draw();
        }
    }

    hardDrop() {
        while (this.currentPiece.canMoveTo(
            this.currentPiece.x, 
            this.currentPiece.y + 1, 
            this.currentPiece.shape, 
            this.board
        )) {
            this.currentPiece.y++;
            this.score += 2;
        }
        this.placePiece();
        this.clearLines();
        this.spawnNewPiece();
        this.updateDisplay();
        this.draw();
    }

    placePiece() {
        for (let y = 0; y < this.currentPiece.shape.length; y++) {
            for (let x = 0; x < this.currentPiece.shape[y].length; x++) {
                if (this.currentPiece.shape[y][x]) {
                    const boardY = this.currentPiece.y + y;
                    const boardX = this.currentPiece.x + x;
                    if (boardY >= 0) {
                        this.board[boardY][boardX] = this.currentPiece.color;
                    }
                }
            }
        }
    }

    clearLines() {
        let linesCleared = 0;

        for (let y = BOARD_HEIGHT - 1; y >= 0; y--) {
            if (this.board[y].every(cell => cell !== 0)) {
                this.board.splice(y, 1);
                this.board.unshift(Array(BOARD_WIDTH).fill(0));
                linesCleared++;
                y++;
            }
        }

        if (linesCleared > 0) {
            this.lines += linesCleared;
            this.score += [0, 100, 300, 500, 800][linesCleared] * this.level;
            this.level = Math.floor(this.lines / 10) + 1;
            this.dropInterval = Math.max(50, 1000 - (this.level - 1) * 50);
            this.updateDisplay();
        }
    }

    spawnNewPiece() {
        this.currentPiece = this.nextPiece;
        this.nextPiece = new TetrisPiece();
        this.drawNextPiece();

        if (!this.currentPiece.canMoveTo(
            this.currentPiece.x, 
            this.currentPiece.y, 
            this.currentPiece.shape, 
            this.board
        )) {
            this.gameOver();
        }
    }

    gameOver() {
        this.gameRunning = false;
        document.getElementById('final-score').textContent = this.score;
        document.getElementById('game-over').classList.remove('hidden');
    }

    restart() {
        this.board = Array(BOARD_HEIGHT).fill().map(() => Array(BOARD_WIDTH).fill(0));
        this.score = 0;
        this.level = 1;
        this.lines = 0;
        this.dropTime = 0;
        this.dropInterval = 1000;
        this.paused = false;

        document.getElementById('game-over').classList.add('hidden');
        this.init();
        this.draw();
    }

    togglePause() {
        this.paused = !this.paused;
    }

    draw() {
        this.ctx.clearRect(0, 0, this.canvas.width, this.canvas.height);

        this.drawBoard();
        this.drawGhost();
        this.drawCurrentPiece();
    }

    drawBoard() {
        for (let y = 0; y < BOARD_HEIGHT; y++) {
            for (let x = 0; x < BOARD_WIDTH; x++) {
                if (this.board[y][x]) {
                    this.ctx.fillStyle = this.board[y][x];
                    this.ctx.fillRect(x * BLOCK_SIZE, y * BLOCK_SIZE, BLOCK_SIZE - 1, BLOCK_SIZE - 1);
                }
            }
        }
    }

    drawGhost() {
        if (!this.currentPiece) return;

        const ghostY = this.currentPiece.getGhostPosition(this.board);
        this.ctx.globalAlpha = 0.3;

        for (let y = 0; y < this.currentPiece.shape.length; y++) {
            for (let x = 0; x < this.currentPiece.shape[y].length; x++) {
                if (this.currentPiece.shape[y][x]) {
                    this.ctx.fillStyle = this.currentPiece.color;
                    this.ctx.fillRect(
                        (this.currentPiece.x + x) * BLOCK_SIZE,
                        (ghostY + y) * BLOCK_SIZE,
                        BLOCK_SIZE - 1,
                        BLOCK_SIZE - 1
                    );
                }
            }
        }

        this.ctx.globalAlpha = 1;
    }

    drawCurrentPiece() {
        if (!this.currentPiece) return;

        this.ctx.fillStyle = this.currentPiece.color;
        for (let y = 0; y < this.currentPiece.shape.length; y++) {
            for (let x = 0; x < this.currentPiece.shape[y].length; x++) {
                if (this.currentPiece.shape[y][x]) {
                    this.ctx.fillRect(
                        (this.currentPiece.x + x) * BLOCK_SIZE,
                        (this.currentPiece.y + y) * BLOCK_SIZE,
                        BLOCK_SIZE - 1,
                        BLOCK_SIZE - 1
                    );
                }
            }
        }
    }

    drawNextPiece() {
        this.nextCtx.clearRect(0, 0, this.nextCanvas.width, this.nextCanvas.height);

        if (!this.nextPiece) return;

        const blockSize = 20;
        const offsetX = (this.nextCanvas.width - this.nextPiece.shape[0].length * blockSize) / 2;
        const offsetY = (this.nextCanvas.height - this.nextPiece.shape.length * blockSize) / 2;

        this.nextCtx.fillStyle = this.nextPiece.color;
        for (let y = 0; y < this.nextPiece.shape.length; y++) {
            for (let x = 0; x < this.nextPiece.shape[y].length; x++) {
                if (this.nextPiece.shape[y][x]) {
                    this.nextCtx.fillRect(
                        offsetX + x * blockSize,
                        offsetY + y * blockSize,
                        blockSize - 1,
                        blockSize - 1
                    );
                }
            }
        }
    }

    updateDisplay() {
        document.getElementById('score').textContent = this.score;
        document.getElementById('level').textContent = this.level;
        document.getElementById('lines').textContent = this.lines;
    }
}

