     2→<html>
     3→<head>
     4→  <title>QR Generator</title>
     5→  <style>
     6→    * { box-sizing: border-box; }
     7→    body { width: 350px; padding: 15px; font-family: Arial; transition: background-color 0.3s; }
     8→    .container { text-align: center; }
     9→    h2 { text-align: center; margin: 0 0 10px; }
    10→    #qrcode { margin: 15px auto; display: inline-block; animation: fadeIn 0.5s; width: 200px; height: 200px; position: relative; }
    11→    input[type="text"], select { margin: 5px 0; padding: 8px; border-radius: 5px; border: 1px solid #ccc; }
    12→    .top-controls { display: flex; align-items: center; justify-content: space-between; margin-bottom: 10px; }
    13→    input[type="text"] { width: 75%; }
    14→    select#size { width: 60px; text-align: center; padding: 5px; }
    15→    button { padding: 5px 10px; border: none; border-radius: 3px; background: #4CAF50; color: white; cursor: pointer; font-size: 12px; }
    16→    button:hover { background: #45a049; }
    17→    .dark-mode { background: #333; color: white; }
    18→    .dark-mode input[type="text"], .dark-mode select { background: #444; color: white; border-color: #555; }
    19→    .dark-mode button { background: #388E3C; }
    20→    .dark-mode button:hover { background: #2F7D32; }
    21→    .dark-mode .color-option { border: 1px solid #ffffff; }
    22→    .light-mode .color-option { border: 1px solid #000000; }
    23→    .toggle-switch { position: absolute; top: 5px; right: 15px; display: inline-flex; align-items: center; font-size: 12px; }
    24→    .toggle-switch span { margin-right: 5px; }
    25→    .toggle-switch input { display: none; }
    26→    .toggle-switch label { position: relative; display: inline-block; width: 30px; height: 15px; background: #ccc; border-radius: 15px; cursor: pointer; }
    27→    .toggle-switch label::after { content: ''; position: absolute; width: 12px; height: 12px; background: white; border-radius: 50%; top: 1.5px; left: 1.5px; transition: 0.3s; }
    28→    .toggle-switch input:checked + label { background: #4CAF50; }
    29→    .toggle-switch input:checked + label::after { left: 16.5px; }
    30→    @keyframes fadeIn { from { opacity: 0; } to { opacity: 1; } }
    31→    .error { color: red; font-size: 12px; display: none; }
    32→    #recentDropdown { width: 100%; margin: 5px 0; }
    33→    .color-sections { display: flex; justify-content: center; align-items: flex-start; margin: 10px 0; }
    34→    .color-section { flex: 1; margin: 0 10px; text-align: center; }
    35→    .color-section label { display: block; font-weight: bold; margin-bottom: 5px; }
    36→    .color-options { display: flex; justify-content: center; gap: 5px; }
    37→    .color-option { width: 15px; height: 15px; border-radius: 50%; display: inline-block; cursor: pointer; }
    38→    .color-option:hover { border-color: #000; }
    39→    .vertical-line { width: 1px; background: #ccc; margin: 0 10px; height: 100%; }
    40→    .dark-mode .vertical-line { background: #555; }
    41→    .download-controls { display: flex; justify-content: center; align-items: center; margin: 10px 0; gap: 10px; }
    42→  </style>
