    75→          <div class="color-option" style="background: #00ff00;" data-color="#00ff00"></div>
    76→          <div class="color-option" style="background: #0000ff;" data-color="#0000ff"></div>
    77→          <div class="color-option" style="background: #ffff00;" data-color="#ffff00"></div>
    78→          <div class="color-option" style="background: #ff00ff;" data-color="#ff00ff"></div>
    79→          <div class="color-option" style="background: #00ffff;" data-color="#00ffff"></div>
    80→        </div>
    81→      </div>
    82→    </div>
    83→    <div class="download-controls">
    84→      <button id="download">Download</button>
    85→      <select id="size">
    86→        <option value="64">64px</option>
    87→        <option value="128">128px</option>
    88→        <option value="256">256px</option>
    89→        <option value="512">512px</option>
    90→      </select>
    91→    </div>
    92→    <select id="recentDropdown">
    93→      <option value="">Recent Codes</option>
    94→    </select>
    95→    <div id="error" class="error">Input too long or invalid. Max 200 characters.</div>
    96→  </div>
    97→  <script src="qrcode.js"></script>
    98→  <script src="popup.js"></script>
    99→</body>
