document.addEventListener('DOMContentLoaded', () => {
  const input = document.getElementById('input');
  const qrcodeDiv = document.getElementById('qrcode');
  const copyLinkBtn = document.getElementById('copyLink');
  const downloadBtn = document.getElementById('download');
  const sizeSelect = document.getElementById('size');
  const darkModeSwitch = document.getElementById('darkMode');
  const colorPickerDark = document.getElementById('colorPickerDark');
  const colorPickerLight = document.getElementById('colorPickerLight');
  const recentDropdown = document.getElementById('recentDropdown');
  const errorDiv = document.getElementById('error');
  const logoUpload = document.getElementById('logoUpload');
  const clearLogo = document.getElementById('clearLogo');
  const circularDots = document.getElementById('circularDots');
  let currentDarkColor = '#000000';
  let currentLightColor = '#ffffff';
  let history = [];
  let customLogoUrl = null;

  // Load history from storage
  chrome.storage.sync.get(['history'], (result) => {
    if (result.history) history = result.history;
    updateHistory();
  });

  // Pre-fill with current URL and generate QR on load with logo
  chrome.tabs.query({ active: true, currentWindow: true }, (tabs) => {
    input.value = tabs[0].url;
    const faviconUrl = tabs[0].favIconUrl;
    generateQR(faviconUrl);
  });

  function generateQR(imageUrl) {
    const text = input.value.trim();
    if (text.length > 200 || !text) {
      errorDiv.style.display = 'block';
      return;
    } else {
      errorDiv.style.display = 'none';
    }
    qrcodeDiv.innerHTML = ''; // Clear existing QR code

    // Use custom logo if available, otherwise use provided imageUrl
    const logoToUse = customLogoUrl || imageUrl;

    const qrcode = new QRCode(qrcodeDiv, {
      text: text || 'https://example.com',
      width: 200,
      height: 200,
      colorDark: currentDarkColor,
      colorLight: currentLightColor,
      correctLevel: QRCode.CorrectLevel.H
    });

    // Wait for QR code canvas to be available, then apply effects
    setTimeout(() => {
      const canvas = qrcodeDiv.querySelector('canvas');
      if (!canvas) {
        console.log('Canvas not found');
        return;
      }

      // Apply circular dots if enabled
      if (circularDots.checked) {
        makeDotsCircular();
      }

      // Add logo if available
      if (logoToUse) {
        addLogoToCanvas(canvas, logoToUse);
      }
    }, 150); // Increased delay to ensure canvas is rendered
    addToHistory(text);
  }

  function addLogoToCanvas(canvas, imageUrl) {
    const ctx = canvas.getContext('2d');
    const img = new Image();
    img.crossOrigin = "Anonymous";
    img.onload = () => {
      console.log('Logo loaded:', imageUrl);
      const qrSize = 200;
      const logoSize = qrSize * 0.2;
      const x = (qrSize - logoSize) / 2;
      const y = (qrSize - logoSize) / 2;

      // White background circle
      ctx.fillStyle = '#ffffff';
      ctx.beginPath();
      ctx.arc(x + logoSize/2, y + logoSize/2, logoSize/2 + 5, 0, 2 * Math.PI);
      ctx.fill();

      // Circular clipped logo
      ctx.save();
      ctx.beginPath();
      ctx.arc(x + logoSize/2, y + logoSize/2, logoSize/2, 0, 2 * Math.PI);
      ctx.clip();
      ctx.drawImage(img, x, y, logoSize, logoSize);
      ctx.restore();
    };
    img.onerror = () => {
      console.log('Logo failed to load:', imageUrl);
    };
    img.src = imageUrl;
  }

  function makeDotsCircular() {
    const canvas = qrcodeDiv.querySelector('canvas');
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    const imageData = ctx.getImageData(0, 0, 200, 200);
    const data = imageData.data;

    // Clear canvas
    ctx.fillStyle = currentLightColor;
    ctx.fillRect(0, 0, 200, 200);

    // Draw circular dots
    ctx.fillStyle = currentDarkColor;
    const moduleSize = 8; // Approximate module size

    for (let y = 0; y < 200; y += moduleSize) {
      for (let x = 0; x < 200; x += moduleSize) {
        const pixelIndex = (y * 200 + x) * 4;
        if (pixelIndex < data.length) {
          const isDark = data[pixelIndex] < 128;

          if (isDark) {
            ctx.beginPath();
            ctx.arc(x + moduleSize/2, y + moduleSize/2, moduleSize/2.5, 0, 2 * Math.PI);
            ctx.fill();
          }
        }
      }
    }
    console.log('Circular dots applied');
  }

  function addToHistory(text) {
    if (!history.includes(text)) {
      history.unshift(text);
      if (history.length > 10) history.pop();
      chrome.storage.sync.set({ history: history });
      updateHistory();
    }
  }

  function updateHistory() {
    recentDropdown.innerHTML = '<option value="">Recent Codes</option>';
    history.forEach((item, index) => {
      const url = new URL(item);
      const domain = url.hostname || 'Unknown';
      const option = document.createElement('option');
      option.value = item;
      option.textContent = `${domain}: ${item.substring(0, 20)}${item.length > 20 ? '...' : ''}`;
      recentDropdown.appendChild(option);
    });
  }

  copyLinkBtn.addEventListener('click', () => {
    navigator.clipboard.writeText(input.value || 'https://example.com').then(() => {
      alert('Link copied to clipboard!');
    });
  });

  downloadBtn.addEventListener('click', () => {
    const canvas = qrcodeDiv.querySelector('canvas');
    if (canvas) {
      const tempCanvas = document.createElement('canvas');
      const size = parseInt(sizeSelect.value);
      tempCanvas.width = size;
      tempCanvas.height = size;
      const ctx = tempCanvas.getContext('2d');
      ctx.drawImage(canvas, 0, 0, size, size);
      const link = document.createElement('a');
      link.download = 'qrcode.png';
      link.href = tempCanvas.toDataURL('image/png');
      link.click();
    }
  });

  recentDropdown.addEventListener('change', () => {
    if (recentDropdown.value) {
      input.value = recentDropdown.value;
      chrome.tabs.query({ active: true, currentWindow: true }, (tabs) => {
        const faviconUrl = tabs[0].favIconUrl ? `https://cors-anywhere.herokuapp.com/${tabs[0].favIconUrl}` : 'https://cors-anywhere.herokuapp.com/https://www.google.com/favicon.ico';
        generateQR(faviconUrl);
      });
      recentDropdown.value = ''; // Reset dropdown after selection
    }
  });

  darkModeSwitch.addEventListener('change', () => {
    document.body.classList.toggle('dark-mode', darkModeSwitch.checked);
    document.body.classList.toggle('light-mode', !darkModeSwitch.checked);
  });

  document.querySelectorAll('#colorPickerDark .color-option').forEach(option => {
    option.addEventListener('click', (e) => {
      currentDarkColor = e.target.getAttribute('data-color');
      chrome.tabs.query({ active: true, currentWindow: true }, (tabs) => {
        const faviconUrl = tabs[0].favIconUrl ? `https://cors-anywhere.herokuapp.com/${tabs[0].favIconUrl}` : 'https://cors-anywhere.herokuapp.com/https://www.google.com/favicon.ico';
        generateQR(faviconUrl);
      });
    });
  });

  document.querySelectorAll('#colorPickerLight .color-option').forEach(option => {
    option.addEventListener('click', (e) => {
      currentLightColor = e.target.getAttribute('data-color');
      chrome.tabs.query({ active: true, currentWindow: true }, (tabs) => {
        const faviconUrl = tabs[0].favIconUrl ? `https://cors-anywhere.herokuapp.com/${tabs[0].favIconUrl}` : 'https://cors-anywhere.herokuapp.com/https://www.google.com/favicon.ico';
        generateQR(faviconUrl);
      });
    });
  });

  // Logo upload handler
  logoUpload.addEventListener('change', (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => {
        customLogoUrl = event.target.result;
        chrome.tabs.query({ active: true, currentWindow: true }, (tabs) => {
          const faviconUrl = tabs[0].favIconUrl || 'https://cors-anywhere.herokuapp.com/https://www.google.com/favicon.ico';
          generateQR(faviconUrl);
        });
      };
      reader.readAsDataURL(file);
    }
  });

  // Clear custom logo handler
  clearLogo.addEventListener('click', () => {
    customLogoUrl = null;
    logoUpload.value = '';
    chrome.tabs.query({ active: true, currentWindow: true }, (tabs) => {
      const faviconUrl = tabs[0].favIconUrl || 'https://cors-anywhere.herokuapp.com/https://www.google.com/favicon.ico';
      generateQR(faviconUrl);
    });
  });

  // Circular dots handler
  circularDots.addEventListener('change', () => {
    chrome.tabs.query({ active: true, currentWindow: true }, (tabs) => {
      const faviconUrl = tabs[0].favIconUrl || 'https://cors-anywhere.herokuapp.com/https://www.google.com/favicon.ico';
      generateQR(faviconUrl);
    });
  });
});

