input.new_string:
function makeDotsCircular() {
  const canvas = qrcodeDiv.querySelector('canvas');
  if (!canvas) {
    console.log('Canvas not found for circular dots');
    return;
  }

  const ctx = canvas.getContext('2d');
  const imageData = ctx.getImageData(0, 0, 200, 200);
  const data = imageData.data;

  console.log('Converting to circular dots...');

  // Clear the entire canvas
  ctx.clearRect(0, 0, 200, 200);

  // Fill with background color
  ctx.fillStyle = currentLightColor;
  ctx.fillRect(0, 0, 200, 200);

  // Set dot color
  ctx.fillStyle = currentDarkColor;

  // Process the QR code in modules (approximate 8px per module for 200px QR)
  const moduleSize = 8;
  for (let row = 0; row < 25; row++) {
    for (let col = 0; col < 25; col++) {
      const x = col * moduleSize;
      const y = row * moduleSize;

      // Sample the center pixel of this module
      const centerX = x + moduleSize/2;
      const centerY = y + moduleSize/2;
      const pixelIndex = (Math.floor(centerY) * 200 + Math.floor(centerX)) * 4;

      if (pixelIndex < data.length) {
        const isDark = data[pixelIndex] < 128; // Check if it's a dark pixel

        if (isDark) {
          // Draw a circle instead of a square
          ctx.beginPath();
          ctx.arc(centerX, centerY, moduleSize/2.5, 0, 2 * Math.PI);
          ctx.fill();
        }
      }
    }
  }
  console.log('Circular dots conversion complete');
}

