class QRGenerator {
  constructor() {
    this.bgColor = '#ffffff';
    this.dotColor = '#000000';
    this.customLogo = null;
    this.isCircular = false;
    this.history = [];

    this.initElements();
    this.bindEvents();
    this.loadHistory();
    this.initializeWithCurrentTab();
  }

  initElements() {
    this.textInput = document.getElementById('textInput');
    this.copyBtn = document.getElementById('copyBtn');
    this.qrContainer = document.getElementById('qrcode');
    this.bgColors = document.getElementById('bgColors');
    this.dotColors = document.getElementById('dotColors');
    this.logoInput = document.getElementById('logoInput');
    this.clearLogo = document.getElementById('clearLogo');
    this.circularDots = document.getElementById('circularDots');
    this.downloadBtn = document.getElementById('downloadBtn');
    this.sizeSelect = document.getElementById('sizeSelect');
    this.recentSelect = document.getElementById('recentSelect');
  }

  bindEvents() {
    // Text input
    this.textInput.addEventListener('input', () => this.generateQR());

    // Copy button
    this.copyBtn.addEventListener('click', () => this.copyText());

    // Background color selection
    this.bgColors.addEventListener('click', (e) => {
      if (e.target.classList.contains('color-dot')) {
        this.selectColor(this.bgColors, e.target);
        this.bgColor = e.target.dataset.color;
        this.generateQR();
      }
    });

    // Dot color selection
    this.dotColors.addEventListener('click', (e) => {
      if (e.target.classList.contains('color-dot')) {
        this.selectColor(this.dotColors, e.target);
        this.dotColor = e.target.dataset.color;
        this.generateQR();
      }
    });

    // Logo upload
    this.logoInput.addEventListener('change', (e) => this.handleLogoUpload(e));
    this.clearLogo.addEventListener('click', () => this.clearCustomLogo());

    // Circular dots
    this.circularDots.addEventListener('change', () => {
      this.isCircular = this.circularDots.checked;
      this.generateQR();
    });

    // Download
    this.downloadBtn.addEventListener('click', () => this.downloadQR());

    // Recent selection
    this.recentSelect.addEventListener('change', (e) => {
      if (e.target.value) {
        this.textInput.value = e.target.value;
        this.generateQR();
        e.target.value = '';
      }
    });
  }

  selectColor(container, selectedDot) {
    container.querySelectorAll('.color-dot').forEach(dot => dot.classList.remove('active'));
    selectedDot.classList.add('active');
  }

  async initializeWithCurrentTab() {
    try {
      const tabs = await chrome.tabs.query({ active: true, currentWindow: true });
      if (tabs[0]?.url) {
        this.textInput.value = tabs[0].url;
        this.generateQR();
      }
    } catch (error) {
      console.log('Could not get current tab URL');
      this.textInput.value = 'https://example.com';
      this.generateQR();
    }
  }

  generateQR() {
    const text = this.textInput.value.trim() || 'https://example.com';

    // Clear previous QR code
    this.qrContainer.innerHTML = '';

    // Generate basic QR code
    const qr = new QRCode(this.qrContainer, {
      text: text,
      width: 200,
      height: 200,
      colorDark: this.dotColor,
      colorLight: this.bgColor,
      correctLevel: QRCode.CorrectLevel.H
    });

    // Apply modifications after generation
    setTimeout(() => {
      this.applyModifications();
    }, 100);

    this.addToHistory(text);
  }

  applyModifications() {
    const canvas = this.qrContainer.querySelector('canvas');
    if (!canvas) return;

    let needsUpdate = false;

    if (this.isCircular) {
      this.makeDotsCircular(canvas);
      needsUpdate = true;
    }

    if (this.customLogo) {
      // For logo, we need to wait for image to load
      this.addLogoToCanvas(canvas);
    } else if (needsUpdate) {
      // Force immediate visual update for circular dots
      this.forceCanvasUpdate(canvas);
    }
  }

  makeDotsCircular(canvas) {
    const ctx = canvas.getContext('2d');
    const imageData = ctx.getImageData(0, 0, 200, 200);
    const data = imageData.data;

    // Clear and redraw with circles
    ctx.fillStyle = this.bgColor;
    ctx.fillRect(0, 0, 200, 200);
    ctx.fillStyle = this.dotColor;

    const moduleCount = 25;
    const moduleSize = 200 / moduleCount;

    for (let row = 0; row < moduleCount; row++) {
      for (let col = 0; col < moduleCount; col++) {
        const x = col * moduleSize;
        const y = row * moduleSize;

        // Sample the center of this module
        const centerX = x + moduleSize / 2;
        const centerY = y + moduleSize / 2;
        const pixelIndex = (Math.floor(centerY) * 200 + Math.floor(centerX)) * 4;

        if (pixelIndex < data.length && data[pixelIndex] < 128) {
          const radius = moduleSize * 0.4;
          ctx.beginPath();
          ctx.arc(centerX, centerY, radius, 0, 2 * Math.PI);
          ctx.fill();
        }
      }
    }
  }

  forceCanvasUpdate(canvas) {
    // Trigger a visual update by briefly changing canvas properties
    const originalTransform = canvas.style.transform;
    canvas.style.transform = 'scale(1.001)';
    setTimeout(() => {
      canvas.style.transform = originalTransform;
    }, 1);
  }

  addLogoToCanvas(canvas) {
    const ctx = canvas.getContext('2d');
    const img = new Image();

    img.onload = () => {
      const logoSize = 40;
      const x = (200 - logoSize) / 2;
      const y = (200 - logoSize) / 2;

      // Draw white background circle
      ctx.fillStyle = '#ffffff';
      ctx.beginPath();
      ctx.arc(x + logoSize/2, y + logoSize/2, logoSize/2 + 3, 0, 2 * Math.PI);
      ctx.fill();

      // Draw logo with circular clipping
      ctx.save();
      ctx.beginPath();
      ctx.arc(x + logoSize/2, y + logoSize/2, logoSize/2, 0, 2 * Math.PI);
      ctx.clip();
      ctx.drawImage(img, x, y, logoSize, logoSize);
      ctx.restore();

      // Refresh display after logo is added
      this.refreshDisplay(canvas);
    };

    img.src = this.customLogo;
  }

  refreshDisplay(canvas) {
    // Force canvas to repaint by slightly modifying its style
    const originalStyle = canvas.style.display;
    canvas.style.display = 'none';
    canvas.offsetHeight; // Trigger reflow
    canvas.style.display = originalStyle;

    // Also try forcing a redraw
    const ctx = canvas.getContext('2d');
    const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height);
    ctx.putImageData(imageData, 0, 0);
  }

  handleLogoUpload(event) {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        this.customLogo = e.target.result;
        this.generateQR();
      };
      reader.readAsDataURL(file);
    }
  }

  clearCustomLogo() {
    this.customLogo = null;
    this.logoInput.value = '';
    this.generateQR();
  }

  copyText() {
    const text = this.textInput.value || 'https://example.com';
    navigator.clipboard.writeText(text).then(() => {
      // Visual feedback
      const originalText = this.copyBtn.textContent;
      this.copyBtn.textContent = 'Copied!';
      setTimeout(() => {
        this.copyBtn.textContent = originalText;
      }, 1000);
    });
  }

  downloadQR() {
    const canvas = this.qrContainer.querySelector('canvas');
    if (!canvas) return;

    const size = parseInt(this.sizeSelect.value);
    const downloadCanvas = document.createElement('canvas');
    downloadCanvas.width = size;
    downloadCanvas.height = size;

    const ctx = downloadCanvas.getContext('2d');
    ctx.drawImage(canvas, 0, 0, size, size);

    const link = document.createElement('a');
    link.download = `qrcode-${size}px.png`;
    link.href = downloadCanvas.toDataURL('image/png');
    link.click();
  }

  addToHistory(text) {
    if (!this.history.includes(text)) {
      this.history.unshift(text);
      if (this.history.length > 10) this.history.pop();
      this.saveHistory();
      this.updateRecentDropdown();
    }
  }

  loadHistory() {
    try {
      chrome.storage.sync.get(['qr_history'], (result) => {
        if (result.qr_history) {
          this.history = result.qr_history;
          this.updateRecentDropdown();
        }
      });
    } catch (error) {
      console.log('Storage not available');
    }
  }

  saveHistory() {
    try {
      chrome.storage.sync.set({ qr_history: this.history });
    } catch (error) {
      console.log('Storage not available');
    }
  }

  updateRecentDropdown() {
    this.recentSelect.innerHTML = '<option value="">Recent codes...</option>';
    this.history.forEach(item => {
      const option = document.createElement('option');
      option.value = item;
      option.textContent = item.length > 50 ? item.substring(0, 50) + '...' : item;
      this.recentSelect.appendChild(option);
    });
  }
}

// Initialize when DOM is loaded
document.addEventListener('DOMContentLoaded', () => {
  new QRGenerator();
});

