h2 { text-align: center; margin: 0 0 15px; font-size: 20px; font-weight: normal; }

/* Dark mode toggle */
.dark-mode-toggle {
  position: absolute;
  top: 15px;
  right: 15px;
  display: flex;
  align-items: center;
  gap: 8px;
  font-size: 14px;
}
.toggle-switch {
  position: relative;
  width: 50px;
  height: 24px;
  background: #ccc;
  border-radius: 24px;
  cursor: pointer;
  transition: background 0.3s;
}
.toggle-switch.active { background: #4CAF50; }
.toggle-slider {
  position: absolute;
  top: 2px;
  left: 2px;
  width: 20px;
  height: 20px;
  background: white;
  border-radius: 50%;
  transition: transform 0.3s;
}
.toggle-switch.active .toggle-slider { transform: translateX(26px); }

