  let dotColor = '#000000';
  let customLogo = "icon128.png";
  let history = [];

  // Initialize with current tab URL
  console.log('About to call initializeApp');
  initializeApp();

  function initializeApp() {
    chrome.tabs.query({ active: true, currentWindow: true }, (tabs) => {
      if (tabs[0]?.url) {
        textInput.value = tabs[0].url;
      } else {
        textInput.value = 'https://example.com';
      }
      generateQR();
    });
    loadHistory();
  }

