    console.log('Updating recent dropdown, history length:', history.length);
    recentList.innerHTML = '';

    if (history.length === 0) {
      recentList.innerHTML = '<div class="recent-item" style="color: #999; cursor: default;">No recent codes</div>';
      return;
    }

    history.forEach((item, index) => {
      // Handle both old string format and new object format
      const text = typeof item === 'string' ? item : item.text;
      const hasLogo = typeof item === 'object' && item.logo && item.logo !== "icon128.png";

      const recentItem = document.createElement('div');
      recentItem.className = 'recent-item';
      const textSpan = document.createElement('span');
      textSpan.className = 'recent-text';
      textSpan.textContent = `${hasLogo ? 'IMG ' : ''}${text.length > 45 ? text.substring(0, 45) + '...' : text}`;

