console.log('Script loading...');

document.addEventListener('DOMContentLoaded', () => {
  console.log('Extension loaded!');
  // Elements
  const textInput = document.getElementById('textInput');
  const copyBtn = document.getElementById('copyBtn');
  const qrContainer = document.getElementById('qrcode');
  const darkModeToggle = document.getElementById('darkModeToggle');
  const bgColors = document.getElementById('bgColors');
  const dotColors = document.getElementById('dotColors');
  const bgColorPicker = document.getElementById('bgColorPicker');
  const dotColorPicker = document.getElementById('dotColorPicker');
  const logoInput = document.getElementById('logoInput');
  const clearLogo = document.getElementById('clearLogo');
  const downloadBtn = document.getElementById('downloadBtn');
  const sizeSelect = document.getElementById('sizeSelect');
  const recentSelect = document.getElementById('recentSelect');
  const clearAllBtn = document.getElementById('clearAllBtn');

  // State
  let bgColor = '#ffffff';
  let dotColor = '#000000';
  let customLogo = "icon128.png";
  let history = [];
  let lastSavedText = '';

  // Initialize with current tab URL
  console.log('About to call initializeApp');
  initializeApp();

  function initializeApp() {
    chrome.tabs.query({ active: true, currentWindow: true }, (tabs) => {
      if (tabs[0]?.url) {
        textInput.value = tabs[0].url;
      } else {
        textInput.value = 'https://example.com';
      }
      generateQR(); // Don't add initial URL to history
    });
    loadHistory();
  }

  function generateQR() {
    const text = textInput.value.trim() || 'https://example.com';

    // Clear container
    qrContainer.innerHTML = '';

    if (customLogo && customLogo !== null) {
      // Generate QR with logo using custom canvas approach
      generateQRWithLogo(text);
    } else {
      // Generate normal QR code
      const qr = new QRCode(qrContainer, {
        text: text,
        width: 200,
        height: 200,
        colorDark: dotColor,
        colorLight: bgColor,
        correctLevel: QRCode.CorrectLevel.H
      });
    }

    // Add to history only when text actually changes (not on every input event)
    if (text && text !== 'https://example.com' && text !== lastSavedText) {
      addToHistory(text);
      lastSavedText = text;
    }
  }

  function generateQRWithLogo(text) {
    // Create a temporary container for the QR code
    const tempDiv = document.createElement('div');
    const qr = new QRCode(tempDiv, {
      text: text,
      width: 200,
      height: 200,
      colorDark: dotColor,
      colorLight: bgColor,
      correctLevel: QRCode.CorrectLevel.H
    });

    setTimeout(() => {
      const tempCanvas = tempDiv.querySelector('canvas');
      if (tempCanvas) {
        // Create final canvas
        const finalCanvas = document.createElement('canvas');
        finalCanvas.width = 200;
        finalCanvas.height = 200;
        const ctx = finalCanvas.getContext('2d');

        // Draw QR code
        ctx.drawImage(tempCanvas, 0, 0);

        // Add logo
        if (customLogo) {
          const img = new Image();
          img.onload = () => {
            const logoSize = 40;
            const x = (200 - logoSize) / 2;
            const y = (200 - logoSize) / 2;

            // White background circle
            ctx.fillStyle = '#ffffff';
            ctx.beginPath();
            ctx.arc(x + logoSize/2, y + logoSize/2, logoSize/2 + 4, 0, 2 * Math.PI);
            ctx.fill();

            // Draw logo with circular clipping
            ctx.save();
            ctx.beginPath();
            ctx.arc(x + logoSize/2, y + logoSize/2, logoSize/2, 0, 2 * Math.PI);
            ctx.clip();
            ctx.drawImage(img, x, y, logoSize, logoSize);
            ctx.restore();

            // Add final canvas to container
            qrContainer.appendChild(finalCanvas);
          };

          if (customLogo === "icon128.png") {
            img.src = chrome.runtime.getURL('icon128.png');
          } else {
            img.src = customLogo;
          }
        } else {
          // Add canvas without logo
          qrContainer.appendChild(finalCanvas);
        }
      }
    }, 100);
  }



  // Event Listeners
  textInput.addEventListener('input', generateQR);

  copyBtn.addEventListener('click', () => {
    const text = textInput.value || 'https://example.com';
    navigator.clipboard.writeText(text).then(() => {
      copyBtn.textContent = 'Copied!';
      setTimeout(() => copyBtn.textContent = 'Copy Link', 1000);
    });
  });

  darkModeToggle.addEventListener('click', () => {
    document.body.classList.toggle('dark-mode');
    darkModeToggle.classList.toggle('active');
  });

  bgColors.addEventListener('click', (e) => {
    if (e.target.classList.contains('color-option')) {
      // Update selection
      bgColors.querySelectorAll('.color-option').forEach(opt => opt.classList.remove('selected'));
      e.target.classList.add('selected');

      bgColor = e.target.dataset.color;
      bgColorPicker.value = bgColor; // Sync color picker
      generateQR();
    }
  });

  dotColors.addEventListener('click', (e) => {
    if (e.target.classList.contains('color-option')) {
      // Update selection
      dotColors.querySelectorAll('.color-option').forEach(opt => opt.classList.remove('selected'));
      e.target.classList.add('selected');

      dotColor = e.target.dataset.color;
      dotColorPicker.value = dotColor; // Sync color picker
      generateQR();
    }
  });

  // Background color picker
  bgColorPicker.addEventListener('change', (e) => {
    bgColor = e.target.value;
    // Remove selection from preset colors
    bgColors.querySelectorAll('.color-option').forEach(opt => opt.classList.remove('selected'));
    generateQR();
  });

  // Dot color picker
  dotColorPicker.addEventListener('change', (e) => {
    dotColor = e.target.value;
    // Remove selection from preset colors
    dotColors.querySelectorAll('.color-option').forEach(opt => opt.classList.remove('selected'));
    generateQR();
  });

  logoInput.addEventListener('change', (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (event) => {
        customLogo = event.target.result;
        generateQR();
      };
      reader.readAsDataURL(file);
    }
  });

  clearLogo.addEventListener('click', () => {
    customLogo = "icon128.png"; // Reset to default icon
    logoInput.value = '';
    generateQR();
  });


  downloadBtn.addEventListener('click', () => {
    const canvas = qrContainer.querySelector('canvas');
    if (canvas) {
      const size = parseInt(sizeSelect.value);
      const downloadCanvas = document.createElement('canvas');
      downloadCanvas.width = size;
      downloadCanvas.height = size;

      const ctx = downloadCanvas.getContext('2d');
      ctx.drawImage(canvas, 0, 0, size, size);

      const link = document.createElement('a');
      link.download = `qrcode-${size}px.png`;
      link.href = downloadCanvas.toDataURL('image/png');
      link.click();
    }
  });

  // Recent select functionality
  recentSelect.addEventListener('change', (e) => {
    const selectedIndex = e.target.value;
    if (selectedIndex !== '') {
      selectHistoryItem(parseInt(selectedIndex));
      e.target.value = ''; // Reset selection
    }
  });

  // Clear all button functionality
  clearAllBtn.addEventListener('click', () => {
    console.log('Clear all clicked');
    history = [];
    saveHistory();
    updateRecentSelect();
  });

  function selectHistoryItem(index) {
    const historyItem = history[index];

    if (historyItem) {
      // Handle both old string format and new object format
      if (typeof historyItem === 'string') {
        textInput.value = historyItem;
      } else {
        // Restore all settings from history
        textInput.value = historyItem.text;
        bgColor = historyItem.bgColor || '#ffffff';
        dotColor = historyItem.dotColor || '#000000';
        customLogo = historyItem.logo || "icon128.png";

        // Update UI to reflect restored settings
        bgColorPicker.value = bgColor;
        dotColorPicker.value = dotColor;

        // Update color option selections
        bgColors.querySelectorAll('.color-option').forEach(opt => {
          opt.classList.toggle('selected', opt.dataset.color === bgColor);
        });
        dotColors.querySelectorAll('.color-option').forEach(opt => {
          opt.classList.toggle('selected', opt.dataset.color === dotColor);
        });

        // Clear file input since we're using stored logo
        logoInput.value = '';
      }

      generateQR();
    }
  }


  // History functions
  function addToHistory(text) {
    // Don't save empty text
    if (!text || text.trim() === '') {
      console.log('Skipping history save for empty text:', text);
      return;
    }

    console.log('Adding to history:', text);
    console.log('Current history before:', history);

    // Create history entry with text, colors, and logo
    const historyEntry = {
      text: text,
      bgColor: bgColor,
      dotColor: dotColor,
      logo: customLogo,
      timestamp: Date.now()
    };

    // Check if this exact text already exists (remove old version)
    const existingIndex = history.findIndex(entry => 
      (typeof entry === 'string' ? entry : entry.text) === text
    );

    console.log('Existing index for text:', existingIndex);

    if (existingIndex !== -1) {
      // Remove old entry
      history.splice(existingIndex, 1);
      console.log('Removed existing entry, history now:', history);
    }

    // Always add new entry at the beginning
    history.unshift(historyEntry);
    if (history.length > 10) history.pop();

    console.log('History after adding:', history);

    saveHistory();
    updateRecentSelect();
  }

  function loadHistory() {
    chrome.storage.sync.get(['qr_history'], (result) => {
      if (result.qr_history) {
        history = result.qr_history;
        updateRecentSelect();
      }
    });
  }

  function saveHistory() {
    chrome.storage.sync.set({ qr_history: history });
  }

  function updateRecentSelect() {
    console.log('Updating recent select, history length:', history.length);

    // Clear existing options except the first one
    recentSelect.innerHTML = '<option value="">Recent Codes</option>';

    if (history.length === 0) {
      const noCodesOption = document.createElement('option');
      noCodesOption.value = '';
      noCodesOption.textContent = 'No recent codes';
      noCodesOption.disabled = true;
      recentSelect.appendChild(noCodesOption);
      return;
    }

    history.forEach((item, index) => {
      // Handle both old string format and new object format
      const text = typeof item === 'string' ? item : item.text;
      const hasLogo = typeof item === 'object' && item.logo && item.logo !== "icon128.png";

      const option = document.createElement('option');
      option.value = index;
      option.textContent = `${hasLogo ? '🖼️ ' : ''}${text.length > 40 ? text.substring(0, 40) + '...' : text}`;
      recentSelect.appendChild(option);
    });
  }
});

