function formatUrlForDisplay(url) {
  try {
    const urlObj = new URL(url);
    let domain = urlObj.hostname;

    // Remove www. prefix for cleaner display, but add it back for consistency
    if (domain.startsWith('www.')) {
      domain = domain.substring(4);
    }

    // Format as www.domain.com/...
    let displayUrl = `www.${domain}`;

    // Add path indicator if there's a path beyond just /
    if (urlObj.pathname && urlObj.pathname !== '/') {
      displayUrl += '/...';
    }

    return displayUrl;
  } catch (e) {
    // If URL parsing fails, use simple truncation
    return url.length > 30 ? url.substring(0, 30) + '...' : url;
  }
}

