      }
      if (callback) callback();
    });
  }

  function formatUrlForDisplay(url) {
    try {
      const urlObj = new URL(url);
      let domain = urlObj.hostname;

      // Remove www. prefix for cleaner display
      if (domain.startsWith('www.')) {
        domain = domain.substring(4);
      }

      // Format as domain.com/...
      let displayUrl = domain;

      // Add path indicator if there's a path beyond just /
      if (urlObj.pathname && urlObj.pathname !== '/') {
        displayUrl += '/...';
      }

      return displayUrl;
    } catch (e) {
      // If URL parsing fails, use simple truncation
      return url.length > 30 ? url.substring(0, 30) + '...' : url;
    }
  }

  function updateRecentSelect() {
    console.log('Updating recent select, history length:', history.length);

    // Clear existing options except the first one
    recentSelect.innerHTML = '<option value="">Recent Codes</option>';

    if (history.length === 0) {
      const noCodesOption = document.createElement('option');
      noCodesOption.value = '';
      noCodesOption.textContent = 'No recent codes';
      noCodesOption.disabled = true;
      recentSelect.appendChild(noCodesOption);
      return;
    }

    history.forEach((item, index) => {
      // Handle both old string format and new object format
      const text = typeof item === 'string' ? item : item.text;
      const hasCustomLogo = typeof item === 'object' && item.logo && item.logo !== "icon128.png";
      const bgColor = typeof item === 'object' ? item.bgColor || '#ffffff' : '#ffffff';
      const dotColor = typeof item === 'object' ? item.dotColor || '#000000' : '#000000';

      // Check if colors are custom (not default black/white)
      const hasCustomColors = bgColor !== '#ffffff' || dotColor !== '#000000';

      const option = document.createElement('option');
      option.value = index;

      // Add number (most recent gets highest number)
      const itemNumber = history.length - index;

      // Build the main text
      let mainText = `${itemNumber}. ${formatUrlForDisplay(text)}`;

      // Build indicators
      let indicators = [];
      if (hasCustomLogo) indicators.push('[Logo]');
      if (hasCustomColors) indicators.push('[Colors]');

      // Combine with spacing to simulate right alignment
      let displayText = mainText;
      if (indicators.length > 0) {
        const indicatorText = indicators.join(' ');
        // Pad with spaces to push indicators to the right (approximate)
        const padding = Math.max(1, 45 - mainText.length - indicatorText.length);
        displayText = mainText + ' '.repeat(padding) + indicatorText;
      }

      option.textContent = displayText;
      recentSelect.appendChild(option);
    });
  }
});

